
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// expression = operands & operators
		// operands = values, variables, numbers, quantity
		// operators = + - * / %
		/*
		 *  + = adição
		 *  - = subtração
		 *  * = multiplicação
		 *  / = divisão
		 *  % = restante
		 */
		
		int n = 10;
		
		n++; // mais 1
		
		n--; // menos 1
		
		n = n / 3; // = 3.33 só como inteiro é apenas 3
		
		n = (n * 3) + 1;
		
		System.out.println(n);
		
	}

}
